        color: this.yellow
      }, {
        name: 'Delayed',
        type: 'column',
        data: this.Ddelayed,
        color: this.red
      }]
    });


  }
  CalculateProgress(list: Action[]): number {
    let rates: number[] = [];
    for (let action of list) {
      rates.push(parseInt(action.Progress.replace('%', '').replace(' ', '')));
    }
    return ((rates.reduce((a, b) => a + b)) / rates.length)
  }

